(() => {
    try {
        // Verifica se existe o campo personalizado
        const campoPersonalizado = $item("0").$node["campos personalizados meetime"].json["data"];
        if (!campoPersonalizado) return $('Pipedrive21').item.json['tipoDeNegocio'] || '';

        // Busca o campo tipoDeNegocio
        const tipoNegocioField = campoPersonalizado.find(item => item.field_key == 'tipoDeNegocio');
        if (!tipoNegocioField || !tipoNegocioField.options) return $('Pipedrive21').item.json['tipoDeNegocio'] || '';

        // Pega o ID do customFields
        const tipoNegocioId = $item("0").$node["Webhook1"].json["content"]["body"]["prospection"]["lead"]["customFields"]["tipoDeNegocio"];
        if (!tipoNegocioId) return $('Ler pessoa').item.json['tipoDeNegocio'] || '';

        // Busca a op√ß√£o pelo ID
        const opcao = tipoNegocioField.options.find(item => item.id == tipoNegocioId);
        if (!opcao || !opcao.label) return $('Ler pessoa').item.json['tipoDeNegocio'] || '';

        return opcao.label;

    } catch (error) {
        return $('Pipedrive21').item.json['tipoDeNegocio'] || '';
    }
})()
}}

