(() => {
    const Jornada = $('Fields Deal').item.json['Jornada do cliente'];

    if (!Jornada || Jornada === '' || Jornada === 'null') {
        return '';
    }

    const campo = $('campos personalizados meetime1').item.json.data
        .find(item => item.field_key == 'jornadaDoCliente');

    if (!campo || !campo.options) return '';

    const opcao = campo.options.find(item => item.label == Jornada);

    return opcao ? `  "jornadaDoCliente": "${opcao.id}",` : '';
})()
}}{{
(() => {
    const tipoNegocio = $('Fields Person').item.json['Tipo de negócio'];

    if (!tipoNegocio || tipoNegocio === '' || tipoNegocio === 'null') {
        return '';
    }

    const campo = $('campos personalizados meetime1').item.json.data
        .find(item => item.field_key == 'tipoDeNegocio');

    if (!campo || !campo.options) return '';

    const opcao = campo.options.find(item => item.label == tipoNegocio);

    return opcao ? `"tipoDeNegocio": "${opcao.id}",` : '';
})()
}}
  "idDealPipe": "{{ $('Fields Deal').item.json['ID Negócio'] }}",
  "anotacoesIniciais": {{
  (function() {
    const value = $('Fields Deal').item.json['Anotações iniciais'];
    if (!value || value === 'N/A' || value === 'undefined' || value.includes('undefined')) {
      return `""`;
    }
    return JSON.stringify(value);
  })()
}},
  "conjunto": "{{ $('Fields Deal').item.json['Conjunto '] }}",
  "pagoOrganico": "{{ $('Fields Deal').item.json['ID de Origem'] }}",
  "content": "{{ $('Fields Deal').item.json.Content }}"
},
    "startImmediately": {{ $item("0").$node["Fields Deal"].json["cadência"] =='out-buffet'? false: $item("0").$node["Fields Deal"].json["cadência"] =='out-arcade'? false:true}}
}

