assignments.value:
={{
  $items("Ler notes").map((item, index) => {
    if (index === 0) {
      // Primeira anotação - com data/hora
      return "Data: " + 
             (item.json.add_time ? 
               new Date(new Date(item.json.add_time).getTime() - 3 * 60 * 60 * 1000).toLocaleString('pt-BR') 
               : "N/A"
             ) + 
             "\n" + 
             item.json.content;
    } else {
      // Demais anotações - apenas o prefixo
      return "Outra anotação: " + item.json.content;
    }
  }).join("\n\n")
}}

