    const Jornada = $('Fields Deal').item.json['Jornada do cliente'];

    if (!Jornada || Jornada === '' || Jornada === 'null') {
        return '';
    }

    const campo = $('campos personalizados meetime1').item.json.data
        .find(item => item.field_key == 'jornadaDoCliente');

    if (!campo || !campo.options) return '';

    const opcao = campo.options.find(item => item.label == Jornada);

    return opcao ? `  "jornadaDoCliente": "${opcao.id}",` : '';
})()
